REPLACE VIEW BMNC_PCODEVW.T99_STATION_LINE_NEW_GROUP
(
   Station_ID
  ,Station_Nme 
  ,Stat_Line_ID
  ,Stat_Line_Nme 
  ,Stat_Net_Typ 
)
AS LOCKING TABLE BMNC_PCODEVW.T99_STAT_STATION_CD
FOR ACCESS SELECT 
    A.Stat_Station_ID(TITLE '')
   ,A.Stat_Station_Nme(TITLE '')
   ,CASE WHEN A.Stat_Station_ID BETWEEN '1525' AND '1537' THEN '75'
         WHEN A.Stat_Station_ID BETWEEN '0663' AND '0677' THEN '76'
    ELSE A.Stat_Line_ID 
    END  AS Stat_Line_ID--(TITLE '')
   ,CASE WHEN A.Stat_Station_ID BETWEEN '1525' AND '1537' THEN '15'
         WHEN B.Stat_Line_ID='15' THEN '15'
         WHEN A.Stat_Station_ID BETWEEN '0663' AND '0677' THEN '6'
         WHEN B.Stat_Line_ID='06' THEN '6'
    ELSE B.Stat_Line_Nme 
    END  AS Stat_Line_Nme--(TITLE '')
   ,CASE WHEN A.Stat_Station_ID BETWEEN '0663' AND '0677' THEN '02'
         WHEN A.Stat_Station_ID BETWEEN '0721' AND '0761' THEN '02'
         WHEN A.Stat_Station_ID BETWEEN '1471' AND '1493' THEN '02'
         WHEN A.Stat_Station_ID BETWEEN '1525' AND '1537' THEN '02'
    ELSE '01'
    END  AS Stat_Net_Typ--(TITLE '/,01-02-')
FROM BMNC_PCODEVW.T99_STAT_STATION_CD A
LEFT JOIN BMNC_PCODEVW.T99_STAT_LINE_CD B
ON A.Stat_Line_ID=B.Stat_Line_ID
AND B.STAT_STD_CD='10';
