FOR ACCESS SELECT Stat_Station_ID(TITLE ''),Stat_Std_Cd(TITLE 'Stat_Std_Cd'),Merged_Ind(TITLE 'Merged_Ind'),Stat_Station_Nme(TITLE ''),Stat_Station_Eng_Name(TITLE ''),Stat_Station_Short_Name(TITLE ''),Stat_Station_Des(TITLE ''),Station_Type_Cd(TITLE ''),Term_Station_Ind(TITLE ''),Traf_Ind(TITLE ''),Up_Station_Seq(TITLE ''),Down_Station_Seq(TITLE ''),Open_Tm(TITLE ''),Close_Tm(TITLE ''),NStation_Upstream_Distance(TITLE ''),NStation_Upstream_Duration(TITLE ''),NStation_Downstream_Distance(TITLE ''),NStation_Downstream_Duration(TITLE ''),Upstream_Stop_Interval(TITLE ''),Downstream_Stop_Interval(TITLE ''),Stat_Line_ID(TITLE '') FROM BMNC_PCODE.T99_STAT_STATION_CD;
REPLACE VIEW bmnc_pcodevw.T99_STAT_STATION_CD_B AS ( 
SEL a.Station_id AS Stat_Station_ID
 ,'10' AS Stat_Std_Cd
 ,CASE WHEN b.line_id IN ('04','93') THEN 2
  ELSE 1 END AS Merged_Ind
 ,a.Station_nme AS Stat_Station_Nme
 ,a.Station_Eng_Name AS Stat_Station_Eng_Name
 ,a.Station_Short_Name AS Stat_Station_Short_Name
 ,a.Station_Des AS Stat_Station_Des
 ,COALESCE(c.Station_Type_Cd,'99') AS Station_Type_Cd
 ,a.Term_Station_Ind AS Term_Station_Ind
 ,a.Traf_Ind AS Traf_Ind
 ,a.Up_Station_Seq AS Up_Station_Seq
 ,a.Down_Station_Seq AS Down_Station_Seq
 ,a.Open_Tm AS Open_Tm
 ,a.Close_Tm AS Close_Tm
 ,COALESCE(c.NStation_Upstream_Distance,0) AS NStation_Upstream_Distance
 ,COALESCE(c.NStation_Upstream_Duration,0) AS NStation_Upstream_Duration
 ,COALESCE(c.NStation_Downstream_Distance,0) AS NStation_Downstream_Distance
 ,COALESCE(c.NStation_Downstream_Duration,0) AS NStation_Downstream_Duration
 ,COALESCE(c.Upstream_Stop_Interval,0) AS Upstream_Stop_Interval
 ,COALESCE(c.Downstream_Stop_Interval,0) AS Downstream_Stop_Interval
 ,b.line_id AS Stat_Line_ID
 FROM bmnc_pdatavw.t20_std_station a
  JOIN bmnc_pdatavw.T20_STATION_LINE_RELA_HIS b
   ON a.station_id = b.station_id
    AND b.End_Tm = CAST('30001231 23:59:59' AS TIMESTAMP(0) FORMAT 'yyyymmddbhh:mi:ss')
LEFT JOIN bmnc_pdatavw.T20_STATION_BASE_INFO_HIS c
 ON a.station_id = c.station_id
 AND c.End_Tm >= CAST('30001231 00:00:00' AS TIMESTAMP(0) FORMAT 'yyyymmddbhh:mi:ss')
WHERE NOT (a.station_id >  '0467' AND b.line_id = '04')--0493
UNION ALL
SEL '93'||a.Station_id AS Stat_Station_ID
 ,'10' AS Stat_Std_Cd
 ,3 AS Merged_Ind
 ,a.Station_nme AS Stat_Station_Nme
 ,a.Station_Eng_Name AS Stat_Station_Eng_Name
 ,a.Station_Short_Name AS Stat_Station_Short_Name
 ,a.Station_Des AS Stat_Station_Des
 ,COALESCE(c.Station_Type_Cd,'99') AS Station_Type_Cd
 ,CASE WHEN (ROW_NUMBER() OVER(PARTITION BY 1 ORDER BY a.station_id )) = 1 THEN 0
    WHEN (ROW_NUMBER() OVER(PARTITION BY 1 ORDER BY a.station_id DESC)) = 1 THEN 1
    ELSE 9 END AS Term_Station_Ind
 ,a.Traf_Ind AS Traf_Ind
 ,ROW_NUMBER() OVER(PARTITION BY 1 ORDER BY a.station_id DESC) AS Up_Station_Seq
 ,ROW_NUMBER() OVER(PARTITION BY 1 ORDER BY a.station_id ) AS Down_Station_Seq
 ,(a.Open_Tm) AS Open_Tm
 ,(a.Close_Tm) AS Close_Tm
 ,COALESCE(c.NStation_Upstream_Distance,0) AS NStation_Upstream_Distance
 ,COALESCE(c.NStation_Upstream_Duration,0) AS NStation_Upstream_Duration
 ,COALESCE(c.NStation_Downstream_Distance,0) AS NStation_Downstream_Distance
 ,COALESCE(c.NStation_Downstream_Duration,0) AS NStation_Downstream_Duration
 ,COALESCE(c.Upstream_Stop_Interval,0) AS Upstream_Stop_Interval
 ,COALESCE(c.Downstream_Stop_Interval,0) AS Downstream_Stop_Interval
 ,'9304' AS Stat_Line_ID
