       ,A.Start_Tm       --  (TITLE '')
       ,CASE WHEN (B.Merged_ind=3 AND C.Merged_ind IN (1,3)) OR  (C.Merged_ind=3 AND B.Merged_Ind IN (1,3)) THEN 3
              WHEN B.Merged_ind=1 and C.Merged_ind=1 THEN 1
              WHEN B.Merged_ind IN (1,2)  AND  C.Merged_Ind IN (1, 2) THEN 2  END AS Merged_Ind
