       ,A.Od_Min_Distance -- (TITLE 'OD')
       ,A.Od_Tkt_Price   -- (TITLE 'OD')
       ,A.End_Tm      -- (TITLE '')
       
 FROM BMNC_PDATAVW.T50_TKT_PRICE A
