  BMNC_PCODEVW.T99_STAT_STATION_RELA_CD  B
  ON  A.Entry_Station_Id=B.Std_Station_Id
 LEFT JOIN
  BMNC_PCODEVW.T99_STAT_STATION_RELA_CD  C
