REPLACE VIEW T20_PHY_STATION AS(
SELECT
               T2.Station_ID_MIX AS Station_ID   
               ,Station_Nme AS Station_Nme         
               ,Station_Des          
               ,Station_Eng_Name          
               ,Station_Short_Name          
               ,Traf_Ind         
               ,CASE WHEN T2.Traf_Ind='1' THEN T2.Station_ID_MIX ELSE ''  END AS Xfer_Station_ID         
               ,Station_Status_Cd        
               ,Open_Tm         
               ,Close_Tm          
               ,Source_Sys_Cd        
               ,Data_Dt         
FROM (
SELECT * FROM 
(
SELECT 
CASE WHEN A.Traf_Ind='1' THEN B.Xfer_Station_ID ELSE A.Station_ID  END AS Station_ID_MIX
,A.*
FROM BMNC_PDATA.T20_STD_STATION A
LEFT JOIN 
(
SELECT 
*
FROM  BMNC_PDATA.T20_STATION_XFER_RELA_HIS 
WHERE ( Station_ID , Xfer_Station_ID ,Start_Tm ) IN (
SELECT
Station_ID
,Xfer_Station_ID
,MAX(Start_Tm)
FROM BMNC_PDATA.T20_STATION_XFER_RELA_HIS 
GROUP BY Station_ID,Xfer_Station_ID)
)B
ON A.Station_ID=B.Station_ID
AND A.Source_Sys_Cd=B.Source_Sys_Cd
)T
WHERE T.Station_ID IN
(SELECT MIN(Station_ID) FROM (
SELECT 
CASE WHEN A.Traf_Ind='1' THEN B.Xfer_Station_ID ELSE A.Station_ID  END AS Station_ID_MIX
,A.*
FROM BMNC_PDATA.T20_STD_STATION A
LEFT JOIN 
(
SELECT 
*
FROM  BMNC_PDATA.T20_STATION_XFER_RELA_HIS 
WHERE ( Station_ID , Xfer_Station_ID ,Start_Tm ) IN (
SELECT
Station_ID
,Xfer_Station_ID
,MAX(Start_Tm)
FROM BMNC_PDATA.T20_STATION_XFER_RELA_HIS 
GROUP BY Station_ID,Xfer_Station_ID)
)B
ON A.Station_ID=B.Station_ID
AND A.Source_Sys_Cd=B.Source_Sys_Cd
)T
GROUP BY Station_ID_MIX)
)T2
)
;
