REPLACE VIEW T20_STATION_PHY_RELA_HIS AS (
--1.
SEL A.STATION_ID
  ,A.OPEN_TM AS START_TM
  ,A.STATION_ID AS PHY_STATION_ID
  ,A.SOURCE_SYS_CD
  ,b.start_tm AS END_TM
FROM BMNC_PDATAVW.T20_STD_STATION A
LEFT JOIN 
(
SEL a.*,ROW_NUMBER() OVER(PARTITION BY a.station_id 
ORDER BY a.start_tm) row_num 
FROM BMNC_PDATAVW.T20_STATION_XFER_RELA_HIS a
) b
 ON A.STATION_ID = B.STATION_ID
 AND b.row_num = 1
WHERE a.open_tm < b.start_tm
UNION
 ALL
--2.
SEL A.STATION_ID
  ,COALESCE(B.START_TM,A.OPEN_TM) AS START_TM
  ,COALESCE(B.XFER_STATION_ID,A.STATION_ID) AS PHY_STATION_ID
  ,A.SOURCE_SYS_CD
  ,COALESCE(B.END_TM,A.CLOSE_TM) AS END_TM
  
FROM BMNC_PDATAVW.T20_STD_STATION A
LEFT JOIN BMNC_PDATAVW.T20_STATION_XFER_RELA_HIS B
 ON A.STATION_ID = B.STATION_ID
);
